pragma solidity ^0.6.0;

import "../openzeppelin-contracts/contracts/token/ERC20/ERC20.sol";

contract Padswap {

    address payable public owner;

    uint public offerCount;
    bool public stopped = false;
    uint public fee;
    address public stakedTokenAddress;

    struct SwapOffer {
        address payable offerer;
        address payable asker;
        uint offerVal;
        uint blockNum;
        State state;
    }

    enum State {Active, Pending, Held, Closed}
    mapping(address => bool) public approvedERC20;
    mapping(uint => SwapOffer) public offers;
    mapping(address => uint[]) public offersByAddress; 
    mapping(address => uint) public offerCountByAddress;
    mapping(address => uint[]) public offersCreatedByAddress; 
    mapping(address => uint) public offersCreatedCountByAddress;

    event LogOfferAdded(uint offerAddedId, address  userAdded);
    event LogOfferAccepted(uint offerAcceptedId, address userAccepted);
    event LogOfferCanceled(uint offerCanceledId, address userCanceled);
    event LogOfferHeld(uint offerCanceledId, address userCanceled);
    event LogOfferUnheld(uint offerCanceledId, address userCanceled);

    modifier onlyOwner (address _address) {require(_address == owner); _;}
    modifier stopInEmergency {require(!stopped); _;}
    modifier onlyInEmergency {require(stopped); _;}

    constructor() public {
       owner = msg.sender;
       offerCount = 0;
    }

    /// @dev Sets circuit breaker value.
    function setStopped(bool _stopped) public onlyOwner(msg.sender) {
        stopped = _stopped;
    }


    /// @dev Sets the token address of the token that needs to be staked.
    function setToken(address _addy) public onlyOwner(msg.sender) {
        stakedTokenAddress = _addy;
    }

    /// @dev Creates an NFT trade offer in the Swap 'order book'.
    /// @return true if successful
    function addOffer(uint _offerVal,  address payable _targetAddress) 
                    public payable stopInEmergency() returns (bool) {

        require(_offerVal > 0, "Must stake more than 0 tokens");
        ERC20 con = ERC20(stakedTokenAddress);
        require(con.balanceOf(msg.sender) >= _offerVal, "Offerer does not have as many tokens as they offered");
        require(con.allowance(msg.sender, address(this)) >= _offerVal, "Offerer has not given the contract allowance to spend enough of their tokens");

        emit LogOfferAdded(offerCount, msg.sender);
        offersByAddress[_targetAddress].push(offerCount);
        offerCountByAddress[_targetAddress] = offerCountByAddress[_targetAddress]+1;
        offersCreatedByAddress[msg.sender].push(offerCount);
        offersCreatedCountByAddress[msg.sender] = offersCreatedCountByAddress[msg.sender]+1;
        offers[offerCount] = SwapOffer({offerer: msg.sender
            , asker: _targetAddress
            , offerVal: _offerVal
            , blockNum: block.number
            , state: State.Active});
        offerCount = offerCount + 1;
        
        return true;
    }

    /// @dev Accepts and executes an already existing offer in the 'order book'.
    function acceptOffer(uint _offerId) public payable stopInEmergency() returns (bool) {
        require(offers[_offerId].offerer != 0x0000000000000000000000000000000000000000, "Offerer must not be the burn address");
        require(offers[_offerId].state == State.Active, "Offer must be active");
        offers[_offerId].state = State.Pending; 
        require(offers[_offerId].asker == msg.sender, "Offer can only be accepted by intended recipient");


        ERC20 con = ERC20(stakedTokenAddress);

        if(offers[_offerId].offerVal > 0) {
            con.transferFrom(offers[_offerId].offerer, address(this), offers[_offerId].offerVal);
            con.transferFrom(offers[_offerId].asker, address(this), offers[_offerId].offerVal);
        }

        offers[_offerId].blockNum = block.number;

        emit LogOfferAccepted(_offerId, msg.sender);
    }

    /// @dev Cancels an offer by setting the state to Closed.
    function cancelOffer(uint _offerId) public stopInEmergency() returns (bool) {
        require(offers[_offerId].offerer == msg.sender || offers[_offerId].asker == msg.sender, "Only the offerer or target can cancel an offer");
        require(offers[_offerId].state == State.Active, "This offer is already closed or accepted");
        offers[_offerId].state = State.Closed;
        emit LogOfferCanceled(_offerId, msg.sender);
    }

    /// @dev Puts a hold on a pending offer if there is currently a snapshot vote being conducted
    function holdOffer(uint _offerId) public onlyOwner(msg.sender) returns (bool) {
        require(offers[_offerId].state == State.Pending, "This offer is not in pending state");
        offers[_offerId].state = State.Held;
        emit LogOfferHeld(_offerId, msg.sender);
    }

    /// @dev Removes a hold on a pending offer if the snapshot vote has concluded
    function unholdOffer(uint _offerId) public onlyOwner(msg.sender) returns (bool) {
        require(offers[_offerId].state == State.Held, "This offer is not in held state");
        offers[_offerId].state = State.Pending;
        emit LogOfferUnheld(_offerId, msg.sender);
    }


    /// @dev Accepts and executes an already existing offer in the 'order book'.
    function finalizeOffer(uint _offerId) public stopInEmergency() returns (bool) {
        require(offers[_offerId].offerer == msg.sender || offers[_offerId].asker == msg.sender || owner == msg.sender, "Only the offerer, target, or contract owner can initiate a finalization");
        require(offers[_offerId].state == State.Pending, "State must be in pending to be finalized");
        require(block.number - offers[_offerId].blockNum >= 518000 || owner == msg.sender , "An offer can only be finalized 90 days after creation");
        ERC20 con = ERC20(stakedTokenAddress);
        con.transfer( offers[_offerId].offerer, offers[_offerId].offerVal);
        con.transfer( offers[_offerId].asker, offers[_offerId].offerVal);
    }

    /// @dev returns true or false (corresponding to whether offer is active or not) given an offer ID.
    /// @return true if active and acceptable, false if closed
    function getOfferState(uint _offerId) public view returns (bool) {
        SwapOffer memory off = offers[_offerId];
        if (off.state == State.Active) {
            return true;
        } else {
            return false;
        }
    }

    /// @dev returns eth offer in wei for a given offer.
    /// @return eth amount in wei that the user who created the trade is offering.
    function getOfferOffVal(uint _offerId) public view returns (uint) {
        SwapOffer memory off = offers[_offerId];
        return off.offerVal;
    }

    /// @dev returns eth offer in wei for a given offer.
    /// @return eth amount in wei that the user who created the trade is offering.
    function getOfferAsker(uint _offerId) public view returns (address) {
        SwapOffer memory off = offers[_offerId];
        return off.asker;
    }

    /// @dev returns eth offer in wei for a given offer.
    /// @return eth amount in wei that the user who created the trade is offering.
    function getOfferOfferer(uint _offerId) public view returns (address) {
        SwapOffer memory off = offers[_offerId];
        return off.offerer;
    }

}
